import React from "react";
import { useState } from "react";


const Home = ()=>{
    
    const [open, isOpen] = useState(false);
    const [Data , set] = useState([])
    const submit= async ()=>{
        await fetch("/api/Dummy/")
        .then(response=>response.json())
        .then(d =>{ set(Data =>[...Data,d["objet"]])});
        console.log(Data);
    };

    
      
    

    return (
        <div>Home
        <button onClick={()=>{ submit();
        isOpen(true);}}>Click</button>
        

        {open === true && Data[0] !== undefined?<table>
            <tr> <th> ID</th>     <th>Name</th>      <th>Email </th>      <th> Age </th>    <th>DOB</th></tr>
            {Data[0].map((item)=>{
                return(
                    <tr> <th>{item.id}</th>  <th>{item.name}</th>    <th>{item.email}</th>    <th>{item.age}</th>  <th> {item.dob}</th></tr>
                )
            })}
</table>:null}
        
        </div>
    )
};

export default Home;
