import React, { useEffect } from "react";
import { useState } from "react";


const Home = ()=>{
    
    const [open, isOpen] = useState(false);
    const [Data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    

    useEffect( ()=>{
        const fetchdata = async()=>{
            await fetch("/api/Dummy/")
            .then(response=>response.json())
            .then(d=>setData(d["objet"]))
            .catch(err=>console.log(err));
            setLoading(false);
        };fetchdata()    },[]);

    

    return (
        <div>Home
        <button onClick={
        isOpen(true)}>Click</button>
        

        {open === true && Data !== undefined?<table>
            <tr> <th> ID</th>     <th>Name</th>      <th>Email </th>      <th> Age </th>    <th>DOB</th></tr>
            {Data.map((item)=>{
                return(
                    <tr> <th>{item.id}</th>  <th>{item.name}</th>    <th>{item.email}</th>    <th>{item.age}</th>  <th> {item.dob}</th></tr>
                )
            })}
</table>:null}
        
        </div>
    )
};

export default Home;
