import React from "react";
import { useState } from "react";


const Home = ()=>{
    
    const [open, isOpen] = useState(false);
    const Data =[]
    const submit = async ()=>{
        let data;
        await fetch("/api/Dummy/")
        .then(response=>response.json())
        .then(d => {data = d["objet"]});
        console.log(data);
        return data;
    };

    
      
    

    return (
        <div>Home
        <button onClick={()=>{let d = submit();
        Data.push(d);
        isOpen(true);}}>Click</button>
        

        {open === true && Data[0] !== undefined?<table>
            <tr> <th> ID</th>     <th>Name</th>      <th>Email </th>      <th> Age </th>    <th>DOB</th></tr>
            {Data[0].map((item)=>{
                return(
                    <tr> <th>{item.id}</th>  <th>{item.name}</th>    <th>{item.email}</th>    <th>{item.age}</th>  <th> {item.dob}</th></tr>
                )
            })}
</table>:null}
        
        </div>
    )
};

export default Home;
