import React from "react";
import { useState } from "react";


const Home = ()=>{
    
    const [open, isOpen] = useState(false);
    const Data =[]
    const submit = async ()=>{
        await fetch("/api/Dummy/")
        .then(response=>response.json())
        .then(d => Data.push(d["objet"]));
        console.log(Data[0]);
    };

    function printStudent(student){
        var ul = document.createElement('ul');
        Object.keys(student).forEach(function (key) {
          var li = document.createElement('li');
          var propertyStudent = student[key];
              li.textContent = key + ' : ' + propertyStudent;
              ul.appendChild(li);
        });
          document.querySelector('body').appendChild(ul);
      }
      
      function printList (list) {
        for (var i = 0; i < list.length; i++) {
            printStudent(list[i]);
        }
      }
      
     console.log(printList(Data));

    return (
        <div>Home
        <button onClick={()=>{submit();
        isOpen(true)}}>Click</button>

        {open === true && Data !== undefined?<table>
            <tr> <th> ID</th>     <th>Name</th>      <th>Email </th>      <th> Age </th>    <th>DOB</th></tr>

</table>:null}
        
        </div>
    )
};

export default Home;
